sp_widget.script:
(function () {
	data.msg = {};
	data.msg.sortingByAsc = gs.getMessage("Sorting by ascending");
	data.msg.sortingByDesc = gs.getMessage("Sorting by descending");

	optCopy(['filter', 'p', 'o', 'd']);

	if (!input) {
		return;
	}

	var murcsUtils = new x_276349_tryanderr.MurcsUtils();
	var clientVO = murcsUtils.getViewObject(options.viewobject);

	data.field_list = clientVO.getFieldList();
  data.title = clientVO.getTitle();
	data.view = clientVO.getViewName();

	data.table = "x_276349_tryanderr_client";

	if (data.field_list) {
		data.fields = data.field_list;
	}


	//data.clickable_columns_form_view_name = options.clickable_columns_form_view_name ? options.clickable_columns_form_view_name.split(",") : [];
	//data.clickable_colums = options.clickable_colums ? options.clickable_colums.split(",") : [];
	//data.enable_column_search = data.enable_column_search || false;
	data.enable_column_search = true;
	data.enable_edit_row = data.enable_edit_row || true;
	data.enable_remove_row = data.enable_remove_row || true;


	data.filter = data.filter || $sp.getValue('filter');
	//data.keywords = data.keywords || $sp.getValue('keywords');
	data.p = data.p || $sp.getValue('p') || 1;
	data.p = parseInt(data.p);
	data.page_index = data.p - 1;
	data.show_new = data.show_new || options.show_new || true;
	var windowSize = data.window_size || $sp.getValue('maximum_entries') || 5;
	windowSize = parseInt(windowSize);
	if (isNaN(windowSize) || windowSize < 1)
		windowSize = 20;
	data.window_size = windowSize;
	data.isClickable = [];
	data.addUser_title = gs.getMessage("Add User");

	var gr;
	if (gs.getProperty("glide.security.ui.filter") == "true") {
		gr = new FilteredGlideRecord(data.table);
		gr.applyRowSecurity();
	} else

	gr = new GlideRecord(data.table);
	if (!gr.isValid()) {
		data.invalid_table = true;
		data.table_label = data.table;
		return;
	}

	data.canCreate = gr.canCreate();
	data.newButtonUnsupported = data.table == "sys_attachment";
	data.table_label = gr.getLabel();
	//data.table_plural = gr.getPlural();
	if (data.filter) {
		if (data.filterACLs) {
			gr = $sp.addQueryString(gr, data.filter);
		} else {
			gr.addEncodedQuery(data.filter);
		}
	}

	if (data.keywords) {
		gr.addQuery('123TEXTQUERY321', data.keywords);
		data.keywords = null;
	}

	if (data.relationship_id) {
		var rel = GlideRelationship.get(data.relationship_id);
		var target = new GlideRecord(data.table);
		var applyTo = new GlideRecord(data.apply_to);
		applyTo.get("sys_id", data.apply_to_sys_id);
		rel.queryWith(applyTo, target); // put the relationship query into target
		gr.addEncodedQuery(target.getEncodedQuery()); // get the query the relationship made for us
	}

	data.fields_array = data.fields.split(',');


	//get object with field names and types
	data.field_type_object = {};
	var grFields = new GlideRecord(data.table);
	for (var k in data.fields_array) {
		var fieldInstance = data.fields_array[k];
		var label = grFields.getElement(fieldInstance).getLabel();
		var name = grFields.getElement(fieldInstance).getName();
		var fieldType = grFields.getElement(fieldInstance).getED().getInternalType();
		data.field_type_object[fieldInstance] = { label: label, type: fieldType, name: name };
	}

	//Filter data by user input

	if (input.searchObject) {
		//gs.addInfoMessage(JSON.stringify(input.searchObject))
		data.querystring = filterRecords(input.searchObject);
		gr.addEncodedQuery(data.querystring);
	}

	if (input.removeRecord) {
		var grRemove = new GlideRecord(data.table);
		grRemove.get(input.recordID);
		grRemove.deleteRecord();
		data.recordRemoved = true;
	}

	if (data.o) {
		if (data.d == "asc")
			gr.orderBy(data.o);
		else
			gr.orderByDesc(data.o);
	}

	data.window_start = data.page_index * data.window_size;
	data.window_end = (data.page_index + 1) * data.window_size;
	gr.chooseWindow(data.window_start, data.window_end);
	gr._query();

	data.row_count = gr.getRowCount();
	data.num_pages = Math.ceil(data.row_count / data.window_size);
	data.column_labels = {};

	// use GlideRecord to get field labels vs. GlideRecordSecure
	var grForLabels = new GlideRecord(data.table);
	for (var i in data.fields_array) {
		var field = data.fields_array[i];
		var ge = grForLabels.getElement(field);
		if (ge == null)
			continue;

		data.column_labels[field] = ge.getLabel();
		//if (data.clickable_colums.indexOf(field) >= 0) {
			//data.isClickable[i] = true;
		//}
	}

	data.references = {};
	data.formViews = {};
	data.list = [];
	while (gr._next()) {
		if (!data.references._loaded) {
			for (var key in data.clickable_colums) {
				data.references[data.clickable_colums[key]] = gr[data.clickable_colums[key]].getReferenceTable();
				data.formViews[data.clickable_colums[key]] = data.clickable_columns_form_view_name[key];
			}
			data.references._loaded = true;
		}
		var record = {};
		$sp.getRecordElements(record, gr, data.fields);
		record.sys_id = gr.getValue('sys_id');
		data.list.push(record);
	}

	if (data.noRecords) {
		data.list = [];
		data.row_count = 0;
		data.num_page = 0;
	}

	//build the final query string for the export function
	data.exportFilter = buildExportFilter();

	function buildExportFilter() {
		queries = [];
		if (data.filter) {
			queries.push(data.filter);
		}
		if (data.querystring) {
			queries.push(data.querystring);
		}

		return queries.join("^");
	}

	// copy to data from input or options
	function optCopy(names) {
		if (input) {
			names.forEach(function (name) {
				data[name] = input[name] || options[name];
			});
		} else {
			names.forEach(function (name) {
				data[name] = options[name];
			});
		}
	}
	// create queries for searchObject, only strings and booleans atm
	function filterRecords(searchObject) {
		var result = '';
		for (var col in searchObject) {
			if (data.field_type_object[col].type === 'boolean') {
				result += col + '=' + searchObject[col] + '^';
			}
			if (data.field_type_object[col].type === 'string') {
				result += col + 'LIKE' + searchObject[col] + '^';
			}
			if (data.field_type_object[col].type === 'string_full_utf8') {
				result += col + 'LIKE' + searchObject[col] + '^';
			}
			if (data.field_type_object[col].type === 'integer') {
				result += col + '=' + searchObject[col] + '^';
			}
			/*
			if (data.field_type_object[col].type === 'translated_field') {
				var value = helperAPI.queryFor(data.table, col, searchObject[col]);
				result += col + '=' + value + '^';
			}
			*/
			if (data.field_type_object[col].type === 'reference') {
				result += col + 'LIKE' + searchObject[col] + '^';
			}
		}
		return result.slice(0, result.length - 1);
	}

})();
