sp_widget.client_script:
function() {
  var c = this;

	  var nodes = new vis.DataSet([
  ]);

  // create an array with edges
  var edges = new vis.DataSet([
  ]);

  // create a network
  var container = document.getElementById('mynetwork');
  var data1 = {
    nodes: nodes,
    edges: edges
  };

	this.server.update().then(function(data) {
		for(var i=0;i<data.sci.length;i++) {
		var item = {id: 6+i, label: data.sci[i]};
		var line  = {from: 6+i, to: 6+i};			

		nodes.add(item);
		edges.add(line);
		}
		var options = {};
    var network = new vis.Network(container, data1, options);
	});

}

sp_widget.script:
(function () {	
  var gr = new GlideRecord('x_276349_tryanderr_catalogserviceconsolidation');

	var sci = [];
	var i=0;
	gr.query();
	while (gr.next()) {
		sci[i] =gr.getValue('id');
		i=i+1;
	}
	data.sci = sci;
})();
