sp_widget.client_script:
function() {
	var c = this;	
	c.display = function() {						
		c.server.update().then(function(data) {		
			console.log("message", data.message)
		})		
	}

	c.display();
}

sp_widget.link:
function(scope, element) {	
	var el = $(element[0]).find('input');	
	scope.$evalAsync(function() {
		el.focus();	
	});
}

sp_widget.script:
(function() {	
